require('./bar.css');
var DataV = require('../../dataV');
var d3 = require('../../libs/d3');
var _ = require('../../util');

function Bar(options) {
  this._init(options);
  this._initLayout();
}

DataV.extend(Bar, {
  render : function () {
    this.svg.classed('bar-chart-g', true);
    this._beforeRender();
    var self = this;
    var opt = this.$options;
    //画轴
    var xAxis, yAxis;
    var xOpt = _.extend({
      orient : 'bottom',
      key : opt.x
    }, (opt.xaxis || {}));
    var yOpt = _.extend({
      orient : 'left',
      key : opt.y
    }, (opt.yaxis || {}));

    xAxis = this.renderAxis(xOpt);
    yAxis = this.renderAxis(yOpt);

    //真正画数据的部分
    var x = xAxis.getX();
    var y = yAxis.getX();

    this.svg.selectAll('.bar')
      .data(this._data)
      .enter()
      .append('rect')
      .attr({
        "class": "bar",
        "x": function (d) { return x(d[opt.x]);},
        "y": function (d) { return y(d[opt.y]); },
        "width": function (d) { return x.rangeBand(); },
        "height": function (d) { return y(yAxis.getMin()) - y(d[opt.y]);},
      });

    this._afterRender();
  }
});

module.exports = Bar;