require('./number.css');
var DataV = require('../../dataV');
var d3 = require('../../libs/d3');
var _ = require('../../util');

function NumberMulti(options) {
  this.numOrient = ['right', 'top', 'left', 'bottom'];
  this._init(options);
  this._initLayout();
}

DataV.extend(NumberMulti, {
  render : function () {
    this._beforeRender();
    var self = this;
    var opt = this.$options;

    var tOpt = _.extend({
      elWidth : 140,
      elHeight : 20,
    }, (opt.title || {}));

    var number = this.renderCom('number');

    var title = this.renderCom('title', tOpt);

    this._afterRender();
  }
});

module.exports = NumberMulti;