require('./scatterplot.css');
var DataV = require('../../dataV');
var d3 = require('../../libs/d3');
var _ = require('../../util');

function Scatterplot(options) {
  this._init(options);
  this._initLayout();
}

DataV.extend(Scatterplot, {
  render : function () {
    this.svg.classed('point-chart-g', true);
    this._beforeRender();
    var self = this;
    var opt = this.$options;
//    画轴
    var xAxis, yAxis;
    var xOpt = _.extend({
      orient : 'bottom',
      key : opt.x
    }, (opt.xaxis || {}));
    var yOpt = _.extend({
      orient : 'left',
      key : opt.y
    }, (opt.yaxis || {}));

    xAxis = this.renderAxis(xOpt);
    yAxis = this.renderAxis(yOpt);

    //真正画数据的部分
    var x = xAxis.getX();
    var y = yAxis.getX();

    this.svg.selectAll('.point')
      .data(this._data)
      .enter()
      .append('circle')
      .attr({
        "class": "point",
        "cx": function (d) { return x(d[opt.x]);},
        "cy": function (d) { return y(d[opt.y]); },
        "r" : 5
      });

    this._afterRender();
  }
});

module.exports = Scatterplot;