var _ = require('../util');
var Com = require('./index');
var d3 = require('../libs/d3');
/**
 * 数字
 * @constructor
 */
function Title(options) {
  this.$options = {
    label  : '可爱滴标题',
    orient : 'top',
    elWidth  : 0,
    elHeight : 0
  };
  this.valid = true;
  this.msg;
  this.el;
  this._init(options);
  this._valid();
}

Com.extend(Title, {
  _valid : function () {
//    var opt = this.$options;
//    var self = this;
  },
  render : function () {
    var opt = this.$options.title;
    if (!this.valid) {
      this.emit('error', {msg: this.msg});
    }

    this.layout('title');
    var text = this.el.append('text')
      .attr({
        class : 'title'
      }).text(opt.label);

    var font = parseInt(text.style('font-size'));
    text.attr('dy', font);
  }
});

module.exports = Title;