var Event = require('./libs/event');
var _ = require('./util/index');
var extend = _.extend;

function DataV () {}

Event.extend(DataV, {
  destroy : function () {
    this.emptyRender();
    this.removeAllComs();
    this._init();
  }
});

var p = DataV.prototype;
extend(p, require('./instance/init'));
extend(p, require('./instance/source'));
extend(p, require('./instance/render'));
extend(p, require('./instance/event'));

module.exports = DataV;
