var exports = module.exports;

exports._initEvent = function () {
  var self = this;
  this._com.on('error', function (err) {
    console.log('com error : ' + err.msg);
    self.emit(err);
    return;
  });

  this.on('error', function (err) {
    console.log('error : ' + err.msg);
    return;
  });
};

exports._removeAllEvent = function () {
  this._com.removeAllListeners();
  this.removeAllListeners();
};

exports.getSvg = function () {
  return this.svg;
};