'use strict';


/**
 * singleMapping 色相一致的色彩映射函数
 * @param  {Object} opt 配置
 * @return {String}   色彩
 */
function singleMapping(opt) {
  opt = opt || {};
  var h = (opt.h === undefined || opt.h === null) ? 180 : opt.h;
  var sFrom = opt.sFrom || 100;
  var sTo = opt.sTo || 20;
  var lFrom = opt.vFrom || 50;
  var lTo = opt.lTo || 100;
  var ds = sTo - sFrom;
  var dv = lTo - lFrom;
  return function (k) {
    return 'hsl(' + h + ',' + (sFrom + k * ds) + '%,' + (lFrom + k * dv) + '%)';
  };
}

/**
 * [colorMapping 色彩函数]
 * @param  {String} type 色彩风格参数
 * @param  {Object} opt  配置
 * @return {Function} 色彩函数
 */
function colorMapping (type, opt) {
  type = type.toLowerCase();

  var mapping = {
    'single': singleMapping
  }[type];
  mapping = mapping ? mapping(opt) : function () {
    return '#099';
  };
  return mapping;
}


module.exports = {
  'colorMapping': colorMapping,
  'singleMapping': singleMapping
};
