var $ = require('jquery');
var _ = require('lodash');

var L = require('leaflet');
var Map = require('./../../map/map');

function map3d(){
  var map = this.map = L.dmap.map('map-container', {
    'center': L.latLng(39.90973623, 116.34521484),
    'zoom': 10,
    'attributionControl': false,
    'crs': L.CRS.EPSG4326,
    'fadeAnimation': false
  });

  map.setBgColor('#f00');
  var tileLayer = L.tileLayer('http://t.mapabc.com/maptile?t=3&x={x}&y={y}&z={z}',{
    reuseTiles: true
  }).addTo(map);
  // map.on('move', function(){
  //   tileLayer.setStyle({

  //   })
  // })

  map.view3d();
}

module.exports = map3d;