'use strict';
/*
jquery leaflet
 */

var $ = require('jquery');
var L = require('./../layer/nameLayer');
var demo = require('./demo');
function test() {//测试svg hatch功能
  var mapObj = demo.getMap();

  var svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
  var path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
  path.setAttribute('d', 'M 0,10 l 10,-10 M -2.5,2.5 l 5,-5 M 7.5,12.5 l 5,-5');
  path.setAttribute('stroke-width', 2);
  path.setAttribute('shape-rendering', 'auto');
  path.setAttribute('stroke', 'darkorange');
  path.setAttribute('stroke-linecap', 'square');
  var patt = document.createElementNS('http://www.w3.org/2000/svg', 'pattern');
  patt.setAttribute('id', 'ykjtc');
  patt.setAttribute('width', '10');
  patt.setAttribute('height', '10');
  patt.setAttribute('patternUnits', 'userSpaceOnUse');
  var defs = document.createElementNS('http://www.w3.org/2000/svg', 'defs');

  var body = document.getElementsByTagName('body')[0];
  defs.appendChild(patt);
  patt.appendChild(path);
  svg.appendChild(defs);
  body.appendChild(svg);

  var circle = L.circle(L.latLng(34.289492, 108.956548)).setRadius(300000).addTo(mapObj);
  $(circle._path).css({
    'fill': 'url(#ykjtc)',
    'fillOpacity': 1
  });
}
module.exports = test;
