/*
jquery leaflet
 */

var $ = require('jquery');
var L = require('leaflet');

function Test() {
  var map = L.map('map-container', {
    'center': L.latLng(39.289492, 118.956548),
    'zoom': 4,
    'attributionControl': false
  });
  L.tileLayer('http://localhost:3000/mapservice?t=0&c=4&x={x}&y={y}&z={z}&v=blue').addTo(map);
  return map;
}


module.exports = Test;
