'use strict';
//对UI的总体控制
var EventEmitter = require('./../libs/event');
var Util = require('./../core/utils');

  function LinkUIGlobal(options){
    this.initEvents();
  }

  EventEmitter.extend(LinkUIGlobal, {
    //注册的一系列全局事件
    events: {
      'node-action': {
        desc: '节点上的活动'
      },
      'node-over':{
        desc: '鼠标经过节点'
      },
      'node-leave':{
         desc: '离开某个节点'
      },
      'panel_com_tap': {
        desc: '在组件编辑器的面板上点击'
      }
    },
    trigger: function (eName, d) {
      if (!eName) return;
      if (eName in this.events) return this.emit(eName, d);
    },
    initEvents: function () {
      // this.on('select-start', function (e) {
      // });
    }
  });
  
  window.linkUIGlobal = module.exports = new LinkUIGlobal();