/**
 */
var merge = require('bcore/utils').merge;
var blending = require('./blending');
var format = require('./format');

  /**
   * updateColorFunc 解析色彩
   * @param  {[type]} a 可以为对象 或colorFrom
   * @param  {[type]} b colorTo
   * @param  {String} c blendingType rgb或hsl 在哪个空间下插值
   * @param  {[type]} d easing函数或关键字符
   */

function getColorFunc(colorFrom, colorTo, blendingType, easing) {
  if (typeof (colorFrom) === 'function') {
    return colorFrom;
  } else if(typeof(colorFrom) === 'object' && !arguments[1] &&!arguments[2]){
    return blending(colorFrom.from, colorFrom.to, colorFrom.space, colorFrom.easing).toFunc();
  } else {
    return blending(colorFrom || '#0ff', colorTo || '#000', blendingType, easing).toFunc();
  }
}

module.exports = merge(format, {blending: blending, getColorFunc: getColorFunc});
